package symboltable;import syntaxtree.Type;public class RamVariable {    String id;    Type type;    int offset;    public RamVariable(String id, Type type) {        this.id = id;        this.type = type;        offset = -1;    }    public String getId() {        return id;    }    public Type type() {        return type;    }        public String toString()     {        if (type instanceof syntaxtree.IdentifierType)            return ((syntaxtree.IdentifierType) type).s + " " + id;        else            return type.getClass() + " " + id;    }        public void setOffset(int o) {        offset = o;    }        public int getOffset() {        return offset;    }} // Variable